import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { GuestService } from '../../service/guest.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import { EventService } from '../../service/event.service';
import { Event } from '../../models/event';
import { MatIcon } from '@angular/material/icon';
import { MatMenuModule } from '@angular/material/menu';

@Component({
  selector: 'app-guest-managment',
  standalone: true,
  imports: [CommonModule, FormsModule, MatIcon, MatMenuModule],
  templateUrl: './guest-managment.component.html',
  styleUrl: './guest-managment.component.scss'
})
export class GuestManagmentComponent implements OnInit {
  eventId: string | null = "";
  guests: any[] = [];
  newGuest: any = { name: '', email: '' };
  editingGuest: any = null;
  event?: Event;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private guestService: GuestService,
    private eventService: EventService
  ) { }

  async ngOnInit() {
    this.eventId = this.route.snapshot.paramMap.get('id');
    await this.eventService.getEvent(this.eventId!).subscribe(
      (data) => {
        this.event = data;
      }
    )
   
    if(this.eventId != null){
      await this.loadGuests();
    }
  }

  async loadGuests() {
    await this.guestService.getGuests(this.eventId!!).subscribe(
      data => {
        this.guests = data
      },
      error => console.error('Error fetching guests', error)
    ); 
  }

  downloadReport() {
    const doc = new jsPDF();
    let descriptionLines;
    const logo = "data:image/png;base64,";

    const aspectRatio = 2000 / 797;
    const logoWidth = 50; 
    const logoHeight = logoWidth / aspectRatio;

    const headerColor = [31, 41, 55];

    const pageWidth = doc.internal.pageSize.getWidth();
    doc.addImage(logo, 'PNG', pageWidth - logoWidth - 14, 10, logoWidth, logoHeight);

    doc.setTextColor(headerColor[0], headerColor[1], headerColor[2]);
    doc.setFontSize(18);
    doc.text('Informe del Evento', 14, 22);

    doc.setFontSize(12);
    doc.setTextColor(0, 0, 0); 
    if (this.event) {
      doc.text(`Nombre del Evento: ${this.event.name}`, 14, 32);
      doc.text(`Fecha: ${this.event.date}`, 14, 42);
      doc.text(`Ubicación: ${this.event.location}`, 14, 52);

      descriptionLines = doc.splitTextToSize(`Descripción: ${this.event.description}`, 180);
      doc.text(descriptionLines, 14, 62);
    }

    const columns = ['Nombre', 'Email', 'Estado'];
    const rows: any[] = [];

    this.guests.forEach(guest => {
      rows.push([
        guest.name,
        guest.email,
        guest.status == 'Attended' ? 'Utilizada' : 'Comprada'
      ]);
    });

    (doc as any).autoTable({
      head: [columns],
      body: rows,
      startY: 90 + (this.event ? descriptionLines.length : 0),
      headStyles: {
        fillColor: headerColor
      }
    });

    doc.save(`${this.event!.name}-report.pdf`);
  }

  goHome(){
    this.router.navigate(['/home']);
  }

  async onDelete(id: string){
    if(confirm("¿Quieres eliminar el invitado?")){
      this.guestService.deleteGuest(this.event!.id, id).subscribe(
        () => this.loadGuests(),
        error => console.error('Error deleting event', error)
      );
    }
  }
}